/// that can make assertions, use broadcasts, and call lemmas.
spec fn test_rec_proof_block(x: int, y: int) -> int
    decreases x,
{
    if x < 1 {
        0
    } else {
        proof {

        }
        test_rec_proof_block(x - 1, y + 1) + 1
    }
}

